INSERT INTO active_ingredients (id, name, description) VALUES (1, 'Fluoxetine', 'A selective serotonin reuptake inhibitor (SSRI) used to treat depression, obsessive-compulsive disorder (OCD), and panic disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (2, 'Sertraline', 'An SSRI antidepressant used to treat depression and anxiety.');
INSERT INTO active_ingredients (id, name, description) VALUES (3, 'Bupropion', 'Atypical antidepressant that affects dopamine and norepinephrine levels in the brain.');
INSERT INTO active_ingredients (id, name, description) VALUES (4, 'Citalopram', 'An SSRI used to treat depression.');
INSERT INTO active_ingredients (id, name, description) VALUES (5, 'Escitalopram', 'An SSRI antidepressant used to treat depression and generalized anxiety disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (6, 'Paroxetine', 'An SSRI used to treat depression, panic attacks, and social anxiety disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (7, 'Venlafaxine', 'A serotonin-norepinephrine reuptake inhibitor (SNRI) used to treat depression and anxiety disorders.');
INSERT INTO active_ingredients (id, name, description) VALUES (8, 'Desvenlafaxine', 'An SNRI antidepressant used to treat major depressive disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (9, 'Duloxetine', 'An SNRI used to treat depression and generalized anxiety disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (10, 'Mirtazapine', 'An atypical antidepressant used primarily for major depressive disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (11, 'Trazodone', 'Used to treat depression. It can also be prescribed for insomnia.');
INSERT INTO active_ingredients (id, name, description) VALUES (12, 'Amitriptyline', 'A tricyclic antidepressant used to treat depression.');
INSERT INTO active_ingredients (id, name, description) VALUES (13, 'Nortriptyline', 'A tricyclic antidepressant used for the treatment of clinical depression.');
INSERT INTO active_ingredients (id, name, description) VALUES (11, 'Aripiprazole', 'An antipsychotic drug used to treat conditions like schizophrenia and bipolar disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (12, 'Risperidone', 'An antipsychotic medication used mainly to treat schizophrenia, bipolar disorder, and irritability in people with autism.');
INSERT INTO active_ingredients (id, name, description) VALUES (13, 'Quetiapine', 'An antipsychotic drug used primarily to treat bipolar disorder, schizophrenia, and major depressive disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (14, 'Olanzapine', 'An antipsychotic medication primarily used to treat schizophrenia and bipolar disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (15, 'Clozapine', 'An antipsychotic medication used mainly to treat schizophrenia in patients who do not respond to other treatments.');
INSERT INTO active_ingredients (id, name, description) VALUES (16, 'Ziprasidone', 'An antipsychotic drug used to treat certain mental/mood disorders such as schizophrenia.');
INSERT INTO active_ingredients (id, name, description) VALUES (17, 'Lurasidone', 'An antipsychotic medication used to treat schizophrenia and depressive episodes associated with bipolar I disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (18, 'Paliperidone', 'An antipsychotic drug used to treat certain mental/mood disorders such as schizophrenia.');
INSERT INTO active_ingredients (id, name, description) VALUES (19, 'Haloperidol', 'A long-standing antipsychotic used in the treatment of schizophrenia and, more acutely, in the treatment of acute psychotic states and delirium.');
INSERT INTO active_ingredients (id, name, description) VALUES (20, 'Iloperidone', 'An antipsychotic drug used for the treatment of schizophrenia.');
INSERT INTO active_ingredients (id, name, description) VALUES (21, 'Asenapine', 'An antipsychotic drug used to treat certain mental/mood disorders such as schizophrenia and bipolar disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (22, 'Brexipiprazole', 'An antipsychotic used to treat conditions like schizophrenia and as an adjunctive treatment in major depressive disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (23, 'Lithium', 'Used for decades, lithium is often a first-line treatment for bipolar disorder, known to help stabilize mood swings.');
INSERT INTO active_ingredients (id, name, description) VALUES (24, 'Valproic Acid', 'Also known as divalproex or valproate, it is used to treat bipolar disorder, and also to prevent migraine headaches.');
INSERT INTO active_ingredients (id, name, description) VALUES (25, 'Lamotrigine', 'Primarily used for bipolar disorder treatment to help delay the occurrence of mood episodes such as depression, mania or hypomania.');
INSERT INTO active_ingredients (id, name, description) VALUES (26, 'Carbamazepine', 'Used for the treatment of bipolar disorder, especially during manic episodes. It is also used for certain types of epilepsy.');
INSERT INTO active_ingredients (id, name, description) VALUES (27, 'Oxcarbazepine', 'Similar to carbamazepine and is used primarily in the treatment of bipolar disorder and epilepsy.');
INSERT INTO active_ingredients (id, name, description) VALUES (28, 'Gabapentin', 'While initially designed for the treatment of epilepsy, it has been found to be beneficial in the treatment of bipolar disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (29, 'Topiramate', 'Although primarily an antiepileptic drug, topiramate can sometimes be used in combination with other medications to treat bipolar disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (30, 'Zonisamide', 'While mainly used as an antiseizure medication, it is sometimes used in bipolar disorder treatment.');
INSERT INTO active_ingredients (id, name, description) VALUES (31, 'Diazepam', 'A benzodiazepine used to treat anxiety disorders, alcohol withdrawal symptoms, or muscle spasms.');
INSERT INTO active_ingredients (id, name, description) VALUES (32, 'Lorazepam', 'Another benzodiazepine, primarily used for anxiety disorders.');
INSERT INTO active_ingredients (id, name, description) VALUES (33, 'Alprazolam', 'Used to treat anxiety and panic disorders, it belongs to the benzodiazepine class.');
INSERT INTO active_ingredients (id, name, description) VALUES (34, 'Clonazepam', 'Often prescribed for panic disorder and certain types of seizures.');
INSERT INTO active_ingredients (id, name, description) VALUES (35, 'Buspirone', 'An anxiolytic that works differently than benzodiazepines and is specifically designed for generalized anxiety disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (36, 'Oxazepam', 'A benzodiazepine used to relieve symptoms of anxiety, including anxiety caused by depression.');
INSERT INTO active_ingredients (id, name, description) VALUES (37, 'Chlordiazepoxide', 'Used to treat anxiety and acute alcohol withdrawal. It is also used to relieve fear and anxiety before surgery.');
INSERT INTO active_ingredients (id, name, description) VALUES (38, 'Temazepam', 'Primarily used for insomnia, but also has anxiolytic properties.');
INSERT INTO active_ingredients (id, name, description) VALUES (39, 'Hydroxyzine', 'An antihistamine that is also used to treat anxiety and tension. It can also be used to help control nausea and vomiting.');
INSERT INTO active_ingredients (id, name, description) VALUES (40, 'Pregabalin', 'Originally used for neuropathic pain, it is also approved to treat generalized anxiety disorder.');
INSERT INTO active_ingredients (id, name, description) VALUES (41, 'LSD (Lysergic acid diethylamide)', 'A powerful hallucinogenic drug known for its intense effects, which researchers are now studying for potential therapeutic benefits.');
INSERT INTO active_ingredients (id, name, description) VALUES (42, 'Psilocybin', 'The active ingredient in "magic mushrooms", being researched for its potential therapeutic effects, especially in the treatment of depression and PTSD.');
INSERT INTO active_ingredients (id, name, description) VALUES (43, 'DMT (Dimethyltryptamine)', 'A powerful hallucinogenic substance that occurs naturally in some plants and animals.');
INSERT INTO active_ingredients (id, name, description) VALUES (44, 'MDMA (3,4-methylenedioxymethamphetamine)', 'Known commonly as ecstasy, it is being researched for potential therapeutic benefits, especially in PTSD.');
INSERT INTO active_ingredients (id, name, description) VALUES (45, 'Mescaline', 'Found in certain cacti, including the peyote cactus, it is known for its hallucinogenic effects.');
INSERT INTO active_ingredients (id, name, description) VALUES (46, 'Ayahuasca', 'A traditional South American brewed drink that contains DMT.');
INSERT INTO active_ingredients (id, name, description) VALUES (47, 'Ibogaine', 'A psychoactive alkaloid derived from the iboga plant, being researched for its potential in addiction treatment.');
INSERT INTO active_ingredients (id, name, description) VALUES (48, 'Salvinorin A', 'The primary psychoactive component of the Salvia divinorum plant.');
INSERT INTO active_ingredients (id, name, description) VALUES (49, '5-MeO-DMT', 'A powerful naturally occurring psychedelic found in certain plants and the secretions of the Colorado River toad.');
INSERT INTO active_ingredients (id, name, description) VALUES (50, '2C-B (2,5-dimethoxy-4-bromophenethylamine)', 'A psychedelic drug first synthesized in the 1970s and known for its unique effects.');
INSERT INTO active_ingredients (id, name, description) VALUES (51, 'Ketamine', 'Originally developed as an anesthetic, ketamine is known for its rapid-acting antidepressant effects and is being used in lower doses for this purpose. It also has hallucinogenic properties.');
